import React from "react";
import axios from "axios";
import "./App.css";

export default class App extends React.Component {
  state = { mapper: { name: [], id: [] } };

  componentDidMount() {
    axios
      .get(
        "https://cors-anywhere.herokuapp.com/https://maps.googleapis.com/maps/api/place/textsearch/json?query=restaurants+in+Charlottesville&opennow&key=AIzaSyBQRbOl8Z5HnrY12zURP84C6Tdwsoy-HUI"
      )
      .then(res => {
        console.log(res.data);
        var theName = res.data.data.map(name => name.theName);
        console.log(theName);
        var ids = res.data.map(id => id.ids);
        this.setState({
          mapper: { name: theName, id: ids }
        });
        console.log(this.state);
      });
  }
  render() {
    console.log(this.state.mapper["name"]);
    return (
      <div>
        <li>{this.state.mapper.name}</li>
      </div>
    );
  }
}
