import React from "react";
import axios from "axios";
import "./App.css";
import { Map, CircleMarker, TileLayer } from "react-leaflet";
import "leaflet/dist/leaflet.css";

const API_KEY = process.env.REACT_APP_API_KEY;
export default class App extends React.Component {
  state = { name: [], id: [], address: [], rating: [], price: [] };

  componentDidMount() {
    axios
      .get(
        "https://cors-anywhere-hclaunch.herokuapp.com/https://maps.googleapis.com/maps/api/place/textsearch/json?query=restaurants+in+Charlottesville&opennow&key=" +
        API_KEY
      )
      .then(res => {
        console.log(res)

        const price = res.data.results.map(thePrice => thePrice.price_level);

        const rating = res.data.results.map(theRating => theRating.rating)
        console.log(rating)

        const address = res.data.results.map(theAddress => theAddress.formatted_address)
        console.log(address)


        const theName = res.data.results.map(names => names.name);
        console.log(theName);

        var ids = res.data.results.map(id => id.id);

        this.setState({
          name: theName,
          id: ids,
          price: price,
          rating: rating,
          address: address
        });
        console.log(this.state);
      });
  }
  render() {
    console.log(this.state.name);
    return (
      <div>
        <Map
          style={{ height: "480px", width: "100%" }}
          zoom={1}
          center={[-0.09, 51.505]}>
        </Map>
        <footer>
          <ul>
            {this.state.name.map(mapper => (<li className="Restaurants">{mapper}</li>))}












          </ul>
        </footer>






      </div>

    );
  }
}

