import React from "react";
import axios from "axios";
import "./App.css";
import { Map, CircleMarker, TileLayer } from "react-leaflet";
import "leaflet/dist/leaflet.css";


const API_KEY = process.env.REACT_APP_API_KEY;
export default class App extends React.Component {
  state = { name: [], lat: [], long: [], address: [], price: [] };

  componentDidMount() {
    axios
      .get(
        "https://cors-anywhere.herokuapp.com/https://maps.googleapis.com/maps/api/place/textsearch/json?query=restaurants+in+Charlottesville&opennow&key=" +
        API_KEY
      )
      .then(res => {
        var lat = res.data.results.map(latitude => latitude.geometry.location.lat);
        console.log(lat);


        var long = res.data.results.map(longitude => longitude.geometry.location.lng);
        console.log(long);

        var theName = res.data.results.map(name => name.name);
        console.log(theName);

        var price = res.data.results.map(thePrice => thePrice.price_level);
        console.log(price);

        var address = res.data.results.map(theAddress => theAddress.formatted_address);
        console.log(address);

        this.setState({
          name: theName,
          lat: lat,
          long: long,
          address: address
        });
        console.log(this.state);
      });
  }
  render() {
    console.log(this.state.name);
    return (
      <div>
        <li>
          {this.state.name.map(maps => (
            <li>
              {maps} is in Charlottesville, VA and currently open for
              business!
              </li>
          ))}
          <ul>
            <li>{this.state.address.map(maps => (<li>Location: {maps}</li>))}
            </li>

            <li>{this.state.price.map(maps => (<li>Price: {maps}</li>))}
            </li>
          </ul>
        </li>
        <footer className="Map">
          <Map
            style={{ height: "300px", width: "150%" }}
            zoom={1}
            center={[-0.09, 51.505]}>
            <TileLayer url="http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <CircleMarker center={[32.0983, -0.09]} />


          </Map>
        </footer>
      </div>
    );
  }
}
